import { StyleSheet, ImageBackground, Image, TouchableOpacity, Alert } from "react-native";
import { NativeBaseProvider, Box, Input, Heading, Divider, AspectRatio, Stack, HStack, Text, Icon, VStack, Center, StatusBar, Button } from 'native-base';
import { Ionicons } from '@expo/vector-icons';
import { FlatList, ScrollView } from "react-native-gesture-handler";
import prizes from './sample';
import { Component, useContext, useEffect } from "react";
import { useState } from "react";
import { UserContext } from "./UserContext";
import * as ImagePicker from 'expo-image-picker';
import * as Permissions from 'expo-permissions';
import * as ImageManipulator from 'expo-image-manipulator';
import * as MediaLibrary from 'expo-media-library';
import firebaseConfig from "../firebaseConfig.tsx";
import { initializeApp } from "firebase/app";
import { getStorage, ref, uploadBytes, getDownloadURL } from "firebase/storage";
import moment from "moment";


initializeApp(firebaseConfig);

export default function TabThreeScreen() {
  const { value, setValue } = useContext(UserContext);
  const [prizesData, setPrizes] = useState([]);

  function Example() {
    const [postData, setPost] = useState({});
    const imageUrl = postData.imageUrl;

    function handlePost(setPrizes) {
      const imageName = `${value.portalId}_${moment(new Date).format("MMDDYYYYhmma")}`
      const storage = getStorage();

      const uploadImage = async () => {
        const img = postData.imageUrl;
        const response = await fetch(postData.imageUrl);
        const blob = await response.blob();
        uploadBytes(ref(storage, `${imageName}`), blob);
        // const url = await ref(`${imageName}`).getDownloadURL();
        // console.log(url);
      }
      uploadImage();
      
      setTimeout(() => {
        getDownloadURL(ref(storage, `${imageName}`))
          .then((url) => {
            (() => {setPost({ ...postData, firebaseImage: url })})
          })
          .catch((error) => {
            console.log(error);
            // Handle any errors
          })
          .then(() => {
            if (!postData.title || !postData.description || !postData.imageUrl || !postData.cost) {
              Alert.alert("Please fill in all fields and add photo!");
            } else {
              fetch(`https://crewcoin.herokuapp.com/store`, {
                method: "POST",
                headers: {
                  authorization: "jwt",
                  credentials: "same-origin",
                  Accept: "application/json",
                  "Content-Type": "application/json",
                  mode: "cors"
                },
                body: JSON.stringify({
                  "title": postData.title,
                  "description": postData.description,
                  "image": postData.firebaseImage,
                  "portalId": value.portalId,
                  "cost": postData.cost,
                }),
              })

                .then(res => res.json())
                .then(res => {
                  if (res.success) {
                    setPrizes(prizesData => [...prizesData, res.store])
                    setPost({ title: "", description: "", imageUrl: "", cost: "" });
                  } else {
                    Alert.alert(
                      "Something went wrong",
                      `Error`,
                      [
                        { text: "OK", onPress: () => console.log("OK Pressed") }
                      ]
                    );
                  }
                })
                .catch(err => {
                  Alert.alert(
                    `Error`,
                    "Please check internet connection!",
                    [

                      { text: "OK", onPress: () => console.log("OK Pressed") }
                    ]
                  )
                }
                );
            }
          }, 3000);
      }
      )};


      let getImageFromCamera = async () => {
        const cameraPermission = await Permissions.askAsync(Permissions.CAMERA);
        const cameraRollPermission = await Permissions.askAsync(Permissions.CAMERA_ROLL);

        if (cameraPermission.status === 'granted' && cameraRollPermission.status === 'granted') {
          const capturedImage = await ImagePicker.launchCameraAsync({
            allowsEditing: true,
            aspect: [1, 1]
          });
          if (!capturedImage.cancelled) {
            console.log(capturedImage);
            processImage(capturedImage.uri);
            MediaLibrary.createAssetAsync(capturedImage.uri);
          }
        }
      }
      let getImageFromGallery = async () => {
        const cameraPermission = await Permissions.askAsync(Permissions.CAMERA);
        const cameraRollPermission = await Permissions.askAsync(Permissions.CAMERA_ROLL);

        if (cameraPermission.status === 'granted' && cameraRollPermission.status === 'granted') {
          const capturedImage = await ImagePicker.launchImageLibraryAsync({
            allowsEditing: true,
            aspect: [1, 1]
          });
          if (!capturedImage.cancelled) {
            processImage(capturedImage.uri);
          }
        }
      }
      let processImage = async (imgUri) => {
        const processedImage = await ImageManipulator.manipulateAsync(imgUri,
          [{ resize: { width: 400 } }],
          { format: ImageManipulator.SaveFormat.PNG }
        );
        setPost({ ...postData, imageUrl: processedImage.uri, image: processedImage });
        console.log("processedImage", processedImage)
      }

      if (value.admin) {
        function TempImage() {
          if (imageUrl) {
            return (
              <Image alt="temp" shadow={9} style={{ width: 300, height: 300, borderRadius: 5 }}
                source={{ uri: imageUrl }} resizeMode="contain" />
            )
          } else {
            return null;
          }
        }

        return (

          <>
            <Box
              shadow={2}
              mt="2"
              mb="2"
              pt="2"
              style={styles.image2}
              maxW="360"
              rounded="lg"
              overflow="hidden"
              borderColor="gray.100"
              borderWidth="1"
              _dark={{
                borderColor: "gray.900",
                backgroundColor: "gray.900",
              }}
              _web={{
                shadow: 2,
                borderWidth: 0,
              }}
              _light={{
                backgroundColor: "gray.50",
              }}
            >
              <Stack w="100%" p="4" space={3}>
                <HStack alignItems="center">
                  <Heading size="md" ml="-1" >
                    Add New Store Item
                  </Heading>
                  <TouchableOpacity onPress={() => { getImageFromCamera() }}>
                    <Image mt="4" style={styles.image3} source={require('../assets/images/camera1.png')} resizeMode="contain" />
                  </TouchableOpacity>
                  <TouchableOpacity onPress={() => { getImageFromGallery() }}>
                    <Image mt="4" style={styles.image4} source={require('../assets/images/camera.png')} resizeMode="contain" />
                  </TouchableOpacity>
                </HStack>
                <Center>
                  <TempImage />
                </Center>
                <Input value={postData.title} onChangeText={(value) => setPost({ ...postData, title: value })} placeholder="Title" />
                <Input value={postData.description} onChangeText={(value) => setPost({ ...postData, description: value })} placeholder="Description" />
                <Input value={postData.cost} onChangeText={(value) => setPost({ ...postData, cost: value })} placeholder="Price" />
                {/* <Divider />
            <Image style={styles.image3} source={require('../assets/images/camera.png')} resizeMode="contain" />
            <Divider /> */}
                <Button onPress={() => { handlePost(setPrizes) }}> Post New Item</Button>
              </Stack>
            </Box>


          </>
        )
      } else {
        return null
      }
    }

    const Prizes = () => {
      const { value, setValue } = useContext(UserContext);
      const [prizesData, setPrizes] = useState([]);
      useEffect(() => {
        fetch(`https://crewcoin.herokuapp.com/store/${value.portalId}`, {
          method: "GET",
          headers: {
            authorization: "jwt",
            credentials: "same-origin",
            Accept: "application/json",
            "Content-Type": "application/json",
            mode: "cors"
          },
        })

          .then(res => res.json())
          .then(res => {
            if (res.success) {
              setPrizes(res.prizes);
              console.log(res.prizes)
            } else {
              Alert.alert(
                "Something went wrong",
                `Error`,
                [
                  { text: "OK", onPress: () => console.log("OK Pressed") }
                ]
              );
            }
          })
          .catch(err => {
            Alert.alert(
              `Error`,
              "Please check internet connection!",
              [
                { text: "OK", onPress: () => console.log("OK Pressed") }
              ]
            )
          }
          );
      }, []);

      let prizes = prizesData.sort(function (a, b) {
        // Turn your strings into dates, and then subtract them
        // to get a value that is either negative, positive, or zero.
        return new Date(b.createdAt) - new Date(a.createdAt);
      });


      function postImage(prizes) {
        if (prizes.image) {
          return (
            <Image
              borderColor="gray.200"
              shadow={9}
              width={300}
              alt={prizes.createdAt}
              source={{ uri: prizes.image }}
              style={{ width: 300, height: 300, borderRadius: 5, resizeMode: 'contain' }}
            />
          )
        } else {
          return null;
        }
      }

      function deleteButton(prize) {
        if (value.admin) {
          return (
            <Button colorScheme="rose" mb="2" onPress={() => {
              Alert.alert(
                "Remove Prize",
                "Are you sure you want to remove this prize?",
                [
                  {
                    text: "Cancel",
                    onPress: () => console.log("Cancel Pressed"),
                    style: "cancel"
                  },
                  {
                    text: "OK", onPress: () => {
                      deletePrize(prize._id)
                    }
                  }
                ],
                { cancelable: false }
              );
            }}>Remove Item</Button>
          )
        } else {
          return null;
        }
      }
      function buyButton(prize) {
        if (!value.admin) {
          return (
            <Button mb="2" onPress={() => {
              Alert.alert(
                "Purchase",
                `Are you sure you want to purchase this ${prize.title}?`,
                [
                  {
                    text: "Cancel",
                    onPress: () => console.log("Cancel Pressed"),
                    style: "cancel"
                  },
                  {
                    text: "OK", onPress: () => {
                      console.log("purchased!")
                    }
                  }
                ],
                { cancelable: false }
              );
            }}>Buy Now</Button>
          )
        } else {
          return null;
        }
      }

      function deletePrize(prize) {
        fetch(`https://crewcoin.herokuapp.com/store/${prize}`, {
          method: "DELETE",
          headers: {
            authorization: "jwt",
            credentials: "same-origin",
            Accept: "application/json",
            "Content-Type": "application/json",
            mode: "cors"
          },
          body: JSON.stringify({
            "prizeId": prize
          }),
        })

          .then(res => res.json())
          .then(res => {
            if (res.success) {
              setPrizes(prizes => prizes.filter(prize => prize._id !== res.prizeId))
              setValue({ ...value, store: res.store })
              Alert.alert(
                "Prize",
                `Deleted`,
                [
                  { text: "OK", onPress: () => console.log("OK Pressed") }
                ]
              );
              console.log(res.prizes)
            } else {
              Alert.alert(
                "Something went wrong",
                `Error`,
                [
                  { text: "OK", onPress: () => console.log("OK Pressed") }
                ]
              );
            }
          })
          .catch(err => {
            Alert.alert(
              `Error`,
              "Please check internet connection!",
              [
                { text: "OK", onPress: () => console.log("OK Pressed") }
              ]
            )
          }
          );
      }

      return (
        prizes.map(prize => {
          return (
            <Box
              shadow={2}
              style={styles.image5}
              mb="2"
              maxW="360"
              rounded="lg"
              overflow="hidden"
              borderColor="gray.300"
              borderWidth="1"
              pt="4"
              _dark={{
                borderColor: "gray.900",
                backgroundColor: "gray.900",
              }}
              _web={{
                shadow: 2,
                borderWidth: 0,
              }}
              _light={{
                backgroundColor: "gray.50",
              }}
            >
              <Box>
                {postImage(prize)}
              </Box>
              <Stack w="330" p="2" space={3}>
                <Stack>

                  <Heading fontSize={16}>
                    {prize.title}
                  </Heading>

                </Stack>
                <Divider />
                <Text fontWeight="400" fontSize={14}>
                  {prize.description}
                </Text>
                <Divider />
                <HStack alignItems="center" space={2} justifyContent="space-between">
                  <HStack alignItems="center">
                    <Text
                      fontWeight="500"
                      color="amber.600"
                      _dark={{
                        color: "amber.600",
                      }}
                      fontWeight="600"
                    >
                      {prize.cost} Crew Coins
                    </Text>
                  </HStack>
                  {buyButton(prize)}
                  {deleteButton(prize)}
                </HStack>
              </Stack>
            </Box>
          )
        })
      )
    }





    return (
      <NativeBaseProvider>
        <AppBar />
        <ImageBackground imageStyle=
          {{ opacity: 0.6 }} style={styles.image2} source={require('../assets/images/splashbg2.png')} resizeMode="cover" >
          <ScrollView>
            <Example prizes={prizes} />
            <Prizes prizes={prizes} />
          </ScrollView>
        </ImageBackground>
        <CardBalance />
      </NativeBaseProvider>
    );
  }
