import { StyleSheet, ImageBackground, Image, View } from "react-native";
import { NativeBaseProvider, Box, Container, Heading, Divider, AspectRatio, Stack, HStack, Text, Icon, VStack, Center, StatusBar, Button } from 'native-base';
import { Ionicons } from '@expo/vector-icons';
import { FlatList, ScrollView } from "react-native-gesture-handler";
import prizes from './sample';



export default function TabThreeScreen() {
  return (
    <NativeBaseProvider>
      <AppBar />
      <Divider />

      <ImageBackground imageStyle=
        {{ opacity: 0.5 }} style={styles.image2} source={require('../assets/images/splashbg.png')} resizeMode="cover" >
        <ScrollView>
          <Example prizes={prizes} />
        </ScrollView>
      </ImageBackground>
      <BalanceBar />
    </NativeBaseProvider>
  );
}
function AppBar() {
  return (
    <>
      <Box safeAreaTop backgroundColor="#f2e6d9" />

      <HStack bg='#e6e600' px="1" justifyContent='space-between' alignItems='center'>
        <HStack space="4" alignItems='center'>
          <Image style={styles.coin} source={require('../assets/images/crewcoinlogo.png')} />
        </HStack>
        <HStack space="4">
          <Text style={styles.icon}>
            David Williams
          </Text>
        </HStack>
      </HStack>

    </>
  )
}

function BalanceBar() {
  return (
    <>
      <Box style={styles.BalanceBar} safeAreaBottom>
        <HStack bg="gray.300">
            <Text style={styles.balanceText}>Balance: 0</Text>
        </HStack>
      </Box>
    </>
  )
}

export const Example = () => {
  return (
    prizes.map(prize => {
      return (
        <Box
          style={styles.image2}
          maxW="80"
          rounded="lg"
          overflow="hidden"
          borderColor="coolGray.200"
          borderWidth="1"
          _dark={{
            borderColor: "coolGray.600",
            backgroundColor: "gray.700",
          }}
          _web={{
            shadow: 2,
            borderWidth: 0,
          }}
          _light={{
            backgroundColor: "gray.50",
          }}
        >
          <Box p="4">
            <AspectRatio w="70%" ratio={10 / 7}>
              <Image
                source={{
                  uri: `${prize.img}`,
                }}
                alt="image"
              />
            </AspectRatio>
          </Box>
          <Stack p="4" space={3}>
            <Stack space={2}>
              <Heading size="md" ml="-1">
                {prize.name}
              </Heading>
            </Stack>
            <Text fontWeight="400">
              {prize.description}
            </Text>
            <HStack alignItems="center" space={4} justifyContent="space-between">
              <HStack alignItems="center">
                <Text
                  color="amber.600"
                  _dark={{
                    color: "amber.200",
                  }}
                  fontWeight="400"
                >
                  {prize.price} Crew Coins
                </Text>
              </HStack>
              <Button>Buy Now!</Button>
            </HStack>
          </Stack>
        </Box>
      )
    })
  )
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: "center",
    justifyContent: "center",
  },
  icon: {
    color: 'black',
    fontSize: 16,
    fontWeight: 'bold',


  },
  balanceText: {
    textAlign: 'center',
    fontSize: 40,
    flex: 1,
    height: '100%',
    
  },
  coin: {
    width: 200,
    resizeMode: 'contain',
    height: 50,
  },
  coingif: {
    size: '100%',
  },
  coingif: {
    width: 10,
  },
  image: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
    width: '100%',
  },
  image2: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
    width: '100%',
    height: '100%',
    marginBottom: 20,
  },
  title: {
    width: 400,
    resizeMode: 'cover',
    marginTop: -100,
    marginBottom: -100,
    margin: -100,
  },
  header: {
    marginTop: '15%',
    marginLeft: '18%',
    justifyContent: 'flex-start',
    alignItems: 'flex-start',
    backgroundColor: '#fff',
  },
  BalanceBar: {
    position: 'absolute',
    bottom: 0,
    left: 0,
    right: 0,
    borderTopLeftRadius: 60,
    borderTopRightRadius: 60,
  },
  center: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
  },
});
